			dateformat = dateformat.replace("YYYY", getFullYear(d));
			dateformat = dateformat.replace("MM", (d.getMonth() + 1));
			dateformat = dateformat.replace("DD", d.getDate());
			//noinspection JSUnresolvedVariable
